import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Warehouses = () => {
    const [warehouses, setWarehouses] = useState([]);
    const [newWarehouse, setNewWarehouse] = useState({ name: '' });
    const [error, setError] = useState(null);

    // Fetch the list of warehouses
    useEffect(() => {
        axios.get('http://localhost:5094/api/warehouse')
            .then(response => {
                setWarehouses(response.data);
            })
            .catch(err => {
                setError('Error fetching warehouses');
            });
    }, []);

    const handleChange = (e) => {
        setNewWarehouse({
            ...newWarehouse,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:5094/api/warehouse', newWarehouse)
            .then(response => {
                setWarehouses([...warehouses, response.data]);
                setNewWarehouse({ name: '' });
            })
            .catch(err => {
                setError('Error adding warehouse');
            });
    };

    const handleDelete = (id) => {
        axios.delete(`http://localhost:5094/api/warehouse/${id}`)
            .then(() => {
                setWarehouses(warehouses.filter(warehouse => warehouse.id !== id));
            })
            .catch(err => {
                setError('Error deleting warehouse');
            });
    };

    return (
        <div>
            <h2>Warehouses</h2>

            {error && <div className="alert alert-danger">{error}</div>}

            {/* Add Warehouse Form */}
            <form onSubmit={handleSubmit} className="mb-4">
                <div className="mb-3">
                    <label htmlFor="warehouse-name" className="form-label">Warehouse Name:</label>
                    <input
                        type="text"
                        id="warehouse-name"
                        name="name"
                        value={newWarehouse.name}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <button type="submit" className="btn btn-primary">Add Warehouse</button>
            </form>

            <h3>Warehouse List</h3>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">Warehouse Name</th>
                        <th scope="col">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {warehouses.map(warehouse => (
                        <tr key={warehouse.id}>
                            <td>{warehouse.name}</td>
                            <td>
                                <Link to={`/warehouse/edit/${warehouse.id}`} className="btn btn-warning btn-sm">Edit</Link>
                                <button onClick={() => handleDelete(warehouse.id)} className="btn btn-danger btn-sm ms-2">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Warehouses;
