import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Products = () => {
    const [products, setProducts] = useState([]);
    const [newProduct, setNewProduct] = useState({ name: '' });
    const [error, setError] = useState(null);

    // Fetch the list of products
    useEffect(() => {
        axios.get('http://localhost:5094/api/product')
            .then(response => {
                setProducts(response.data);
            })
            .catch(err => {
                setError('Error fetching products');
            });
    }, []);

    const handleChange = (e) => {
        setNewProduct({
            ...newProduct,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:5094/api/product', newProduct)
            .then(response => {
                // Add new product to the list (optimistic UI update)
                setProducts([...products, response.data]);
                setNewProduct({ name: '' });  // Clear form
            })
            .catch(err => {
                setError('Error adding product');
            });
    };

    const handleDelete = (id) => {
        axios.delete(`http://localhost:5094/api/product/${id}`)
            .then(() => {
                // Remove the deleted product from the state
                setProducts(products.filter(product => product.id !== id));
            })
            .catch(err => {
                setError('Error deleting product');
            });
    };

    return (
        <div>
            <h2>Products</h2>

            {error && <div className="alert alert-danger">{error}</div>}

            {/* Add Product Form */}
            <form onSubmit={handleSubmit} className="mb-4">
                <div className="mb-3">
                    <label htmlFor="product-name" className="form-label">Product Name:</label>
                    <input
                        type="text"
                        id="product-name"
                        name="name"
                        value={newProduct.name}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <button type="submit" className="btn btn-primary">Add Product</button>
            </form>

            <h3>Product List</h3>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">Product Name</th>
                        <th scope="col">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map(product => (
                        <tr key={product.id}>
                            <td>{product.name}</td>
                            <td>
                                <Link to={`/product/edit/${product.id}`} className="btn btn-warning btn-sm">Edit</Link>
                                <button onClick={() => handleDelete(product.id)} className="btn btn-danger btn-sm ms-2">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Products;
