import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Products = () => {
    const [products, setProducts] = useState([]);
    const [newProduct, setNewProduct] = useState({ name: '' });
    const [error, setError] = useState(null);

    // Fetch the list of products
    useEffect(() => {
        axios.get('http://localhost:5094/api/product')
            .then(response => {
                setProducts(response.data);
            })
            .catch(err => {
                setError('Error fetching products');
            });
    }, []);

    const handleChange = (e) => {
        setNewProduct({
            ...newProduct,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:5094/api/product', newProduct)
            .then(response => {
                // Add new product to the list (optimistic UI update)
                setProducts([...products, response.data]);
                setNewProduct({ name: '' });  // Clear form
            })
            .catch(err => {
                setError('Error adding product');
            });
    };

    return (
        <div>
            <h2>Products</h2>

            {error && <div>{error}</div>}

            {/* Add Product Form */}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Product Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={newProduct.name}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit">Add Product</button>
            </form>

            <h3>Product List</h3>
            <ul>
                {products.map(product => (
                    <li key={product.id}>{product.name}</li>
                ))}
            </ul>
        </div>
    );
};

export default Products;
