import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function ProductEdit() {
  const { id } = useParams(); // To get the product id from the URL
  const navigate = useNavigate();

  const [product, setProduct] = useState({ name: '' });
  const [error, setError] = useState('');

  // Fetch the product to edit when component mounts
  useEffect(() => {
    axios.get(`http://localhost:5096/api/product/${id}`)
      .then(response => {
        setProduct(response.data);
      })
      .catch(error => {
        setError('Error fetching product');
        console.error(error);
      });
  }, [id]);

  // Handle form input change
  const handleChange = (e) => {
    setProduct({
      ...product,
      [e.target.name]: e.target.value,
    });
  };

  // Handle form submission (Save changes)
  const handleSubmit = (e) => {
    e.preventDefault();

    axios.put(`http://localhost:5096/api/product/${id}`, product)
      .then(response => {
        navigate('/products'); // Redirect to products list after saving
      })
      .catch(error => {
        setError('Error updating product');
        console.error(error);
      });
  };

  return (
    <div>
      <h1>Edit Product</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <label>Name</label>
        <input
          type="text"
          name="name"
          value={product.name}
          onChange={handleChange}
        />
        <button type="submit">Save changes</button>
      </form>
    </div>
  );
}

export default ProductEdit;
