import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StockOverview = () => {
    const [stockRecords, setStockRecords] = useState([]);
    const [products, setProducts] = useState([]);
    const [warehouses, setWarehouses] = useState([]);
    const [newStock, setNewStock] = useState({ productId: '', warehouseId: '', quantity: '' });
    const [error, setError] = useState(null);

    // Fetch stock records, products, and warehouses
    useEffect(() => {
        // Fetch stock records
        axios.get('http://localhost:5094/api/stock')
            .then(response => {
                setStockRecords(response.data);
            })
            .catch(err => {
                setError('Error fetching stock records');
            });

        // Fetch products for dropdown
        axios.get('http://localhost:5094/api/product')
            .then(response => {
                setProducts(response.data);
            })
            .catch(err => {
                setError('Error fetching products');
            });

        // Fetch warehouses for dropdown
        axios.get('http://localhost:5094/api/warehouse')
            .then(response => {
                setWarehouses(response.data);
            })
            .catch(err => {
                setError('Error fetching warehouses');
            });
    }, []);

    const handleChange = (e) => {
        setNewStock({
            ...newStock,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const { productId, warehouseId, quantity } = newStock;

        // Validate the input
        if (!productId || !warehouseId || !quantity) {
            setError('Please fill in all fields');
            return;
        }

        // Add new stock record via API
        axios.post('http://localhost:5094/api/stock', { productId, warehouseId, quantity })
            .then(response => {
                // Add the new stock record to the state (optimistic UI update)
                setStockRecords([...stockRecords, response.data]);
                setNewStock({ productId: '', warehouseId: '', quantity: '' });  // Clear the form
                setError(null);
            })
            .catch(err => {
                setError('Error adding stock record');
            });
    };

    return (
        <div>
            <h2>Stock Overview</h2>
            {error && <div className="alert alert-danger">{error}</div>}

            {/* Add Stock Form */}
            <form onSubmit={handleSubmit} className="mb-4">
                <div className="mb-3">
                    <label htmlFor="productId" className="form-label">Product:</label>
                    <select
                        id="productId"
                        name="productId"
                        value={newStock.productId}
                        onChange={handleChange}
                        className="form-select"
                        required
                    >
                        <option value="">Select a product</option>
                        {products.map(product => (
                            <option key={product.id} value={product.id}>{product.name}</option>
                        ))}
                    </select>
                </div>

                <div className="mb-3">
                    <label htmlFor="warehouseId" className="form-label">Warehouse:</label>
                    <select
                        id="warehouseId"
                        name="warehouseId"
                        value={newStock.warehouseId}
                        onChange={handleChange}
                        className="form-select"
                        required
                    >
                        <option value="">Select a warehouse</option>
                        {warehouses.map(warehouse => (
                            <option key={warehouse.id} value={warehouse.id}>{warehouse.name}</option>
                        ))}
                    </select>
                </div>

                <div className="mb-3">
                    <label htmlFor="quantity" className="form-label">Quantity:</label>
                    <input
                        type="number"
                        id="quantity"
                        name="quantity"
                        value={newStock.quantity}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>

                <button type="submit" className="btn btn-primary">Add Stock</button>
            </form>

            {/* Stock Records Table */}
            <h3>Stock Records</h3>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">Product</th>
                        <th scope="col">Warehouse</th>
                        <th scope="col">Quantity</th>
                    </tr>
                </thead>
                <tbody>
                    {stockRecords.map((stock, index) => (
                        <tr key={index}>
                            <td>{stock.product ? stock.product.name : 'Loading...'}</td> {/* Check if product exists */}
                            <td>{stock.warehouse ? stock.warehouse.name : 'Loading...'}</td> {/* Check if warehouse exists */}
                            <td>{stock.quantity}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default StockOverview;
